import React, { useState } from "react";
import "./Home.css";
import Tablet from "../assets/image/Tablet.jpeg";
import Syrup from "../assets/image/syrup.webp";
import Capsule from "../assets/image/Capsule.jpeg";

const Home = () => {
  // Dummy product data
  const products = [
    { id: 1, name: "Tablet", image: Tablet, price: 100 },
    { id: 2, name: "Capsule", image: Capsule, price: 150 },
    { id: 3, name: "Syrup", image: Syrup, price: 120 },
  ];

  const [cart, setCart] = useState([]); // Cart state
  const [searchTerm, setSearchTerm] = useState(""); // Search term state
  const [paymentMode, setPaymentMode] = useState(""); // Payment mode state
  const [formError, setFormError] = useState(""); // Error state for payment selection

  // Function to handle product search
  const onSearchChange = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  // Filtered products based on search
  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm)
  );

  // Function to add product to the cart
  const addToCart = (product) => {
    setCart((prevCart) => {
      const productExists = prevCart.find((item) => item.id === product.id);
      if (productExists) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  // Function to remove product from the cart
  const removeFromCart = (productId) => {
    setCart((prevCart) =>
      prevCart.filter((item) => item.id !== productId)
    );
  };

  // Function to handle payment mode selection
  const handlePaymentChange = (e) => {
    setPaymentMode(e.target.value);
    setFormError(""); // Clear any previous errors
  };

  // Function to handle the checkout process
  const handleCheckout = () => {
    if (!paymentMode) {
      setFormError("Please select a payment mode.");
      return;
    }

    // Placeholder for actual payment processing logic
    alert(`Payment successful with ${paymentMode}. Thank you for your purchase!`);

    // Clear the cart after successful payment
    setCart([]);
    setPaymentMode("");
  };

  return (
    <div className="homepage">
      <header className="header">
        <h1>Medical Shop Management</h1>
        <nav className="navbar">
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/contact">Contact</a></li>
            <li><a href="/about">About</a></li>
            <li><a href="/login">Login/Register</a></li>
          </ul>
        </nav>
      </header>

      {/* Search bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for products..."
          value={searchTerm}
          onChange={onSearchChange}
        />
      </div>

      <section className="categories">
        <h3>Products</h3>
        <div className="category-list">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div className="category-item" key={product.id}>
                <img src={product.image} alt={product.name} className="img-fluid" />
                <p>{product.name} - ${product.price}</p>
                <button onClick={() => addToCart(product)} className="btn btn-primary">
                  Add to Cart
                </button>
              </div>
            ))
          ) : (
            <p>No products found</p>
          )}
        </div>
      </section>

      {/* Display Cart */}
      <section className="cart">
        <h3>Your Cart</h3>
        {cart.length > 0 ? (
          <>
            <ul>
              {cart.map((item) => (
                <li key={item.id}>
                  {item.name} (x{item.quantity}) - ${item.price * item.quantity}
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="btn btn-danger"
                    style={{ marginLeft: "10px" }}
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>

            {/* Payment Mode */}
            <div className="payment-mode">
              <h4>Select Payment Mode:</h4>
              <select value={paymentMode} onChange={handlePaymentChange}>
                <option value="">-- Select Payment Mode --</option>
                <option value="Credit Card">Credit Card</option>
                <option value="Debit Card">Debit Card</option>
                <option value="Cash on Delivery">Cash on Delivery</option>
                <option value="UPI">UPI</option>
              </select>
              {formError && <p style={{ color: "red" }}>{formError}</p>}
            </div>

            {/* Checkout Button */}
            <button onClick={handleCheckout} className="btn btn-success">
              Checkout
            </button>
          </>
        ) : (
          <p>Your cart is empty</p>
        )}
      </section>
    </div>
  );
};

export default Home;
