import React, { useState, useEffect } from "react";
import axios from "axios";

const CheckoutPage = ({ orderId }) => {
  const [orderDetails, setOrderDetails] = useState(null);

  useEffect(() => {
    // Fetch the Razorpay order details from the backend
    const fetchOrderDetails = async () => {
      try {
        const response = await axios.post(
          "http://localhost:8000/medical_store_app/create_order/",
          { order_id: orderId }
        );
        setOrderDetails(response.data);
      } catch (error) {
        console.error("Error fetching order details:", error);
      }
    };

    fetchOrderDetails();
  }, [orderId]);

  const handlePayment = () => {
    // Check if Razorpay is available
    if (typeof Razorpay === "undefined") {
      console.error("Razorpay SDK is not loaded!");
      return;
    }

    const options = {
      key: "rzp_test_a6hVvFHJbT9SAO_kbBhQAZsfvLGhTt0KrcvKo55", // Replace with your Razorpay Key ID
      amount: orderDetails.amount, // Amount in paise
      currency: "INR",
      name: "Medical Store",
      description: "Order Payment",
      order_id: orderDetails.order_id,
      handler: function (response) {
        console.log("Payment successful:", response);
        // Redirect or update your order status here
        // Optionally, send payment details to the backend to update order status
      },
      prefill: {
        name: sessionStorage.getItem("user_name"),
        email: sessionStorage.getItem("user_email"),
        contact: sessionStorage.getItem("user_phone"),
      },
      theme: {
        color: "#4CAF50",
      },
    };

    const razorpay = new Razorpay(options);
    razorpay.open();
  };

  return (
    <div style={styles.container}>
      <h1>Checkout</h1>
      {orderDetails ? (
        <div>
          <h3>Total: â‚¹{orderDetails.amount / 100}</h3>
          <button onClick={handlePayment} style={styles.paymentButton}>
            Pay Now
          </button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  paymentButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default CheckoutPage;
