import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios";

export default function Register() {
  const [formValues, setFormValues] = useState({});
  const [formError, setFormError] = useState({});
  const navigate = useNavigate();

  const onChangeHandler = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });

    if (name === "cpassword") {
      setFormError({
        ...formError,
        password: value !== formValues.password ? "*Passwords do not match" : "",
      });
    } else if (name === "password" && formValues.cpassword) {
      setFormError({
        ...formError,
        password: formValues.cpassword !== value ? "*Passwords do not match" : "",
      });
    }
  };

  const submit = async (e) => {
    e.preventDefault();
    if (Object.keys(formError).length > 0) {
      alert("Please fix the errors before submitting.");
      return;
    }

    try {
      const response = await axios.post(`${apiUrl}/user/`, formValues);
      if (response.status === 200) {
        alert("Registration successful!");
        navigate("/login");
      }
    } catch (error) {
      console.error("Registration error:", error);
      alert("Registration failed. Please try again.");
    }
  };

  return (
    <div
  style={{
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)",
  }}
>
  <div
    className="container mt-5"
    style={{
      width: "550px",
      height: "auto",
      boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)",
      backgroundColor: "#ffffff",
      padding: "40px",
      borderRadius: "15px",
      border: "2px solid #007bff",
    }}
  >
    <form onSubmit={submit} method="post" className="form">
      <div className="row mt-3 mb-3">
        <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" }}>
          <i className="bi bi-person-plus px-3"></i>Register
        </h2>
      </div>

      {/* First Name */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="first_name"
          style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
        >
          First Name:
        </label>
        <input
          required
          type="text"
          name="first_name"
          id="first_name"
          placeholder="Enter your first name"
          className="form-control"
          style={{
            fontSize: "16px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "8px",
          }}
          onChange={onChangeHandler}
        />
      </div>

      {/* Last Name */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="last_name"
          style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
        >
          Last Name:
        </label>
        <input
          type="text"
          name="last_name"
          id="last_name"
          placeholder="Enter your last name"
          className="form-control"
          style={{
            fontSize: "16px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "8px",
          }}
          onChange={onChangeHandler}
        />
      </div>

      {/* Username */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="username"
          style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
        >
          Username:
        </label>
        <input
          required
          type="text"
          name="username"
          id="username"
          placeholder="Enter your username"
          className="form-control"
          style={{
            fontSize: "16px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "8px",
          }}
          onChange={onChangeHandler}
        />
      </div>

      {/* Mobile Number */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="mobile"
          style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
        >
          Mobile Number:
        </label>
        <input
          required
          type="text"
          name="mobile_no"
          id="mobile"
          placeholder="Enter your mobile number"
          pattern="[6789][0-9]{9}"
          title="Please enter a valid phone number"
          className="form-control"
          style={{
            fontSize: "16px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "8px",
          }}
          onChange={onChangeHandler}
        />
        <p className="text-danger" style={{ fontSize: "14px" }}>{formError.mobile_no}</p>
      </div>

      {/* Email */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="email"
          style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
        >
          Email:
        </label>
        <input
          required
          type="email"
          name="email"
          id="email"
          placeholder="Enter your email"
          className="form-control"
          style={{
            fontSize: "16px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "8px",
          }}
          onChange={onChangeHandler}
        />
      </div>

      {/* Password */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="password"
          style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
        >
          Password:
        </label>
        <input
          required
          type="password"
          name="password"
          id="password"
          placeholder="Enter your password"
          pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}"
          title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters"
          className="form-control"
          style={{
            fontSize: "16px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "8px",
          }}
          onChange={onChangeHandler}
        />
      </div>

      {/* Confirm Password */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="cpassword"
          style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
        >
          Confirm Password:
        </label>
        <input
          required
          type="password"
          name="cpassword"
          id="cpassword"
          placeholder="Confirm your password"
          className="form-control"
          style={{
            fontSize: "16px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "8px",
          }}
          onChange={onChangeHandler}
        />
        <p className="text-danger" style={{ fontSize: "14px" }}>{formError.password}</p>
      </div>

      {/* Submit Button */}
      <div className="text-center">
        <button
          type="submit"
          className="btn btn-dark"
          style={{
            width: "100%",
            fontSize: "17px",
            padding: "10px 20px",
            backgroundColor: "#007bff",
            border: "none",
            borderRadius: "8px",
          }}
        >
          Register
        </button>
      </div>
    </form>
  </div>
</div>
  );
}
