import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const API_BASE_URL = "http://localhost:8000/medical_store_app";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCartItems(); // Fetch cart items on component mount
  }, []);

  // Fetch cart items
  const fetchCartItems = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/cart/`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        },
      });
      setCartItems(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching cart items:", error);
      setLoading(false);
    }
  };

  // Add item to cart
  const addToCart = async (medicineId, quantity = 1) => {
    try {
      const response = await axios.post(
        `${API_BASE_URL}/cart/`,
        { medicine_id: medicineId, quantity }, // Ensure this matches backend expectations
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("authToken")}`,
          },
        }
      );
      setCartItems((prevItems) => [...prevItems, response.data]);
    } catch (error) {
      console.error("Error adding item to cart:", error.response?.data || error.message);
    }
  };

  // Remove item from cart
  const removeFromCart = async (id) => {
    try {
      await axios.delete(`${API_BASE_URL}/cart/${id}/`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        },
      });
      setCartItems((prevItems) => prevItems.filter((item) => item.id !== id));
    } catch (error) {
      console.error("Error removing item from cart:", error);
    }
  };

  if (loading) return <p>Loading cart items...</p>;

  return (
    <div className="cart-page" style={styles.cartPage}>
      <header className="navbar">
        <h1>Your Cart</h1>
        <Link to="/UserProductViews" className="nav-button">Back to Home</Link>
      </header>

      {cartItems.length > 0 ? (
        <div className="cart-list" style={styles.cartList}>
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item" style={styles.cartItem}>
              <h3 style={styles.medicineName}>{item.medicine.name}</h3>
              <p style={styles.price}>Price: ${item.medicine.price}</p>
              <p style={styles.quantity}>Quantity: {item.quantity}</p>
              <button
                style={styles.removeButton}
                onClick={() => removeFromCart(item.id)}
              >
                Remove
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p style={styles.emptyCart}>Your cart is empty.</p>
      )}

      {/* Example: Button to add a sample item to the cart */}
      <button
        style={styles.checkoutButton}
        onClick={() => addToCart(1, 1)} // Replace 1 with actual medicineId
      >
        proceed
      </button>
    </div>
  );
}

const styles = {
  cartPage: {
    maxWidth: "800px",
    margin: "2em auto",
    padding: "em",
    background: "#f9f9f9",
    borderRadius: "10px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
  },
  cartTitle: {
    fontSize: "28px",
    marginBottom: "1.5em",
    textAlign: "center",
    fontWeight: "bold",
    color: "#333",
  },
  emptyCart: {
    textAlign: "center",
    fontSize: "18px",
    color: "#888",
  },
  cartList: {
    padding: "0",
    margin: "0",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "1em",
    borderRadius: "8px",
    backgroundColor: "#fff",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
    marginBottom: "1em",
    transition: "transform 0.2s ease-in-out",
  },
  cartItemHover: {
    transform: "scale(1.02)",
  },
  medicineName: {
    fontSize: "20px",
    fontWeight: "500",
    color: "#333",
  },
  price: {
    fontSize: "18px",
    color: "#27ae60",
    fontWeight: "bold",
  },
  quantity: {
    fontSize: "16px",
    color: "#888",
  },
  removeButton: {
    padding: "8px 15px",
    color: "#fff",
    backgroundColor: "#e74c3c",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "14px",
    transition: "background-color 0.2s",
  },
  removeButtonHover: {
    backgroundColor: "#c0392b",
  },
  cartTotal: {
    marginTop: "1.5em",
    fontSize: "20px",
    fontWeight: "600",
    color: "#333",
    textAlign: "right",
  },
  checkoutButton: {
    padding: "12px 25px",
    color: "#fff",
    backgroundColor: "#2ecc71",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "18px",
    width: "100%",
    marginTop: "1.5em",
    textAlign: "center",
    transition: "background-color 0.2s",
  },
  checkoutButtonHover: {
    backgroundColor: "#27ae60",
  },
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#2c3e50",
    color: "#fff",
    borderRadius: "8px 8px 0 0",
    marginBottom: "1em",
  },
  navButton: {
    padding: "8px 15px",
    backgroundColor: "#2980b9",
    color: "#fff",
    textDecoration: "none",
    borderRadius: "4px",
    fontSize: "16px",
    transition: "background-color 0.2s",
  },
  navButtonHover: {
    backgroundColor: "#3498db",
  },
};
