import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // If you're using react-router for navigation

const Cart = ({ cartItems }) => {
  const history = useHistory(); // Using React Router to navigate to payment page

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const totalAmount = calculateTotal();

  const handleProceedToPayment = () => {
    // Redirect to the PaymentPage
    history.push('/payment', { totalAmount });
  };

  return (
    <div>
      <h2>Your Cart</h2>
      <ul>
        {cartItems.map((item) => (
          <li key={item.id}>
            {item.name} - ${item.price} x {item.quantity}
          </li>
        ))}
      </ul>
      <div>
        <strong>Total: ${totalAmount / 100}</strong>
      </div>
      <button onClick={handleProceedToPayment}>Proceed to Payment</button>
    </div>
  );
};

export default Cart;
