import React, { useState, useEffect } from 'react';

// Sample data (you can replace this with data fetched from an API or backend)
const initialProducts = [
  {
    id: 1,
    name: 'Paracetamol',
    price: '5.00',
    description: 'Used for pain relief and fever reduction.',
    inStock: true,
  },
  {
    id: 2,
    name: 'Ibuprofen',
    price: '10.00',
    description: 'Used to reduce inflammation and treat pain.',
    inStock: false,
  },
  {
    id: 3,
    name: 'Aspirin',
    price: '8.50',
    description: 'Used as a pain reliever and to reduce fever or inflammation.',
    inStock: true,
  },
];

function Product() {
  const [products, setProducts] = useState(initialProducts);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    description: '',
    inStock: true,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prevProduct) => ({
      ...prevProduct,
      [name]: name === 'inStock' ? e.target.checked : value,
    }));
  };

  const addProduct = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newProduct),
      });

      if (!response.ok) {
        throw new Error('Failed to add product');
      }

      const addedProduct = await response.json();
      setProducts((prevProducts) => [...prevProducts, addedProduct]);
      setNewProduct({ name: '', price: '', description: '', inStock: true });
      alert(`${newProduct.name} has been added!`);
    } catch (error) {
      console.error(error);
      alert('Error adding product');
    }
  };


  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>Product List</h1>
      
      {/* Admin Product Addition Form */}
      <h2>Add New Product</h2>
      <form onSubmit={addProduct}>
        <div style={{ marginBottom: '10px' }}>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={newProduct.name}
              onChange={handleChange}
              required
            />
          </label>
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>
            Price:
            <input
              type="text"
              name="price"
              value={newProduct.price}
              onChange={handleChange}
              required
            />
          </label>
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>
            Description:
            <input
              type="text"
              name="description"
              value={newProduct.description}
              onChange={handleChange}
              required
            />
          </label>
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>
            In Stock:
            <input
              type="checkbox"
              name="inStock"
              checked={newProduct.inStock}
              onChange={handleChange}
            />
          </label>
        </div>
        <button type="submit" style={{ padding: '10px', backgroundColor: '#007BFF', color: 'white', border: 'none', borderRadius: '4px' }}>
          Add Product
        </button>
      </form>

      {/* Product List */}
      <h2 style={{ marginTop: '20px' }}>Product List</h2>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {products.map((product) => (
          <li key={product.id} style={{ border: '1px solid #ccc', borderRadius: '5px', margin: '10px 0', padding: '15px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
            <h2 style={{ margin: '0', color: '#007BFF' }}>{product.name}</h2>
            <p style={{ margin: '5px 0' }}>{product.description}</p>
            <p style={{ fontWeight: 'bold' }}>
              Price: <span style={{ color: '#28A745' }}>${product.price}</span>
            </p>
            <p style={{ fontStyle: 'italic' }}>{product.inStock ? 'In Stock' : 'Out of Stock'}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Product; // Export the component
