import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../../axios";

export default function ListMedicines() {
  const [medicines, setMedicines] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newMedicine, setNewMedicine] = useState({
    name: "",
    category: "",
    price: "",
    description: "",
    stock_quantity: 0,
  });

  const deleteMedicine = async (id) => {
    await axiosInstance
      .delete(apiUrl + `/medical_store_app/medicine/${id}/`)
      .then((response) => {
        if (response.status === 200) {
          alert("Medicine deleted successfully!");
          setMedicines((prevMedicines) =>
            prevMedicines.filter((medicine) => medicine.id !== id)
          );
        }
      })
      .catch((error) => {
        console.error("Error deleting medicine:", error);
      });
  };

  const getMedicines = async () => {
    await axiosInstance
      .get(apiUrl + `/medical_store_app/medicine/`)
      .then((response) => {
        if (response.status === 200) {
          setMedicines(response.data);
          setLoading(false);
        }
      })
      .catch((error) => {
        if (error.response?.status === 401) {
          generateRefreshToken(error);
        } else {
          console.error("Error fetching medicines:", error);
        }
      });
  };

  const addMedicine = async () => {
    try {
      const response = await axiosInstance.post(
        apiUrl + "/medical_store_app/medicine/",
        newMedicine
      );
      if (response.status === 201) {
        alert("Medicine added successfully!");
        setMedicines((prevMedicines) => [...prevMedicines, response.data]);
        setNewMedicine({
          name: "",
          category: "",
          price: "",
          description: "",
          stock_quantity: 0,
        });
      }
    } catch (error) {
      console.error("Error adding medicine:", error);
      if (error.response) {
        console.log("Error details:", error.response.data); // View error details for debugging
      }
    }
  };

  function ProductRow({ medicine, onDelete, tdStyle, deleteButtonStyle }) {
    return (
      <tr>
        <td style={tdStyle}>{medicine.medicine_name || medicine.name}</td>
        <td style={tdStyle}>{medicine.category || medicine.cat}</td>
        <td style={tdStyle}>{medicine.price}</td>
        <td style={tdStyle}>{medicine.description}</td>
        <td style={tdStyle}>{medicine.stock_quantity || medicine.stock}</td>
        <td style={tdStyle}>
          <button style={deleteButtonStyle} onClick={() => onDelete(medicine.id)}>
            Delete
          </button>
        </td>
      </tr>
    );
  }

  useEffect(() => {
    getMedicines();
  }, []);

  const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "40px",
    fontFamily: "Arial, sans-serif",
  };

  const thStyle = {
    padding: "12px",
    backgroundColor: "#2a2a2a",
    color: "white",
    textAlign: "left",
    borderBottom: "2px solid #ddd",
  };

  const tdStyle = {
    padding: "10px",
    borderBottom: "1px solid #ddd",
    textAlign: "left",
  };

  const deleteButtonStyle = {
    padding: "7px 14px",
    backgroundColor: "#ff9800",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
    transition: "background-color 0.3s ease",
  };

  const containerStyle = {
    margin: "20px",
    fontFamily: "Arial, sans-serif",
  };

  const titleStyle = {
    textAlign: "center",
    marginBottom: "20px",
    color: "#333",
    fontSize: "24px",
  };

  const formStyle = {
    display: "flex",
    flexDirection: "column",
    marginBottom: "20px",
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>View Medicines</h2>
      <div>
        <h3>Add New Medicine</h3>
        <div style={formStyle}>
          <input
            type="text"
            placeholder="Name"
            value={newMedicine.name}
            onChange={(e) =>
              setNewMedicine({ ...newMedicine, name: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Category"
            value={newMedicine.category}
            onChange={(e) =>
              setNewMedicine({ ...newMedicine, category: e.target.value })
            }
          />
          <input
            type="number"
            placeholder="Price"
            value={newMedicine.price}
            onChange={(e) =>
              setNewMedicine({ ...newMedicine, price: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Description"
            value={newMedicine.description}
            onChange={(e) =>
              setNewMedicine({ ...newMedicine, description: e.target.value })
            }
          />
          <input
            type="number"
            placeholder="Stock Quantity"
            value={newMedicine.stock_quantity}
            onChange={(e) =>
              setNewMedicine({ ...newMedicine, stock_quantity: e.target.value })
            }
          />
          <button onClick={addMedicine}>Add Medicine</button>
        </div>
      </div>

      {loading ? (
        <p>Loading medicines...</p>
      ) : (
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>Medicine Name</th>
              <th style={thStyle}>Category</th>
              <th style={thStyle}>Price</th>
              <th style={thStyle}>Description</th>
              <th style={thStyle}>Stock Quantity</th>
              <th style={thStyle}>Action</th>
            </tr>
          </thead>
          <tbody>
            {medicines.length === 0 ? (
              <tr>
                <td colSpan="6" style={{ textAlign: "center" }}>
                  No medicines available
                </td>
              </tr>
            ) : (
              medicines.map((medicine) => (
                <ProductRow
                  key={medicine.id}
                  medicine={medicine}
                  onDelete={deleteMedicine}
                  tdStyle={tdStyle}
                  deleteButtonStyle={deleteButtonStyle}
                />
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}
