import React, { useState } from "react";

const UserProductViews = ({ listMedicines }) => {
  const [cart, setCart] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [paymentMode, setPaymentMode] = useState("");
  const [formError, setFormError] = useState("");
  const [showReceipt, setShowReceipt] = useState(false);

  // Handle product search
  const onSearchChange = (e) => setSearchTerm(e.target.value.toLowerCase());

  // Filter medicines based on search
  const filteredMedicines = listMedicines.filter((medicine) =>
    medicine.name.toLowerCase().includes(searchTerm)
  );

  // Add medicine to the cart
  const addToCart = (medicine) => {
    setCart((prevCart) => {
      const medicineExists = prevCart.find((item) => item.id === medicine.id);
      if (medicineExists) {
        return prevCart.map((item) =>
          item.id === medicine.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...medicine, quantity: 1 }];
    });
  };

  // Remove medicine from the cart
  const removeFromCart = (medicineId) => setCart((prevCart) => prevCart.filter((item) => item.id !== medicineId));

  // Handle payment mode selection
  const handlePaymentChange = (e) => {
    setPaymentMode(e.target.value);
    setFormError("");
  };

  // Handle checkout process
  const handleCheckout = () => {
    if (!paymentMode) {
      setFormError("Please select a payment mode.");
      return;
    }
    setShowReceipt(true);
  };

  // Calculate total price of cart items
  const totalPrice = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div style={styles.pageContainer}>
      {/* Search Bar */}
      <div style={styles.searchBar}>
        <input
          type="text"
          placeholder="Search for medicines..."
          value={searchTerm}
          onChange={onSearchChange}
          style={styles.searchInput}
        />
      </div>

      {/* Medicine Section */}
      <section style={styles.medicineSection}>
        <h3>Available Medicines</h3>
        <div style={styles.medicineList}>
          {filteredMedicines.length > 0 ? (
            filteredMedicines.map((medicine) => (
              <div key={medicine.id} style={styles.medicineItem}>
                <img src={medicine.image} alt={medicine.name} style={styles.medicineImage} />
                <p>{medicine.name} - ${medicine.price}</p>
                <button onClick={() => addToCart(medicine)} style={styles.addButton}>
                  Add to Cart
                </button>
              </div>
            ))
          ) : (
            <p>No medicines found</p>
          )}
        </div>
      </section>

      {/* Cart Section */}
      <section style={styles.cartSection}>
        <h3>Your Cart</h3>
        {cart.length > 0 ? (
          <>
            <ul style={styles.cartList}>
              {cart.map((item) => (
                <li key={item.id} style={styles.cartItem}>
                  {item.name} (x{item.quantity}) - ${item.price * item.quantity}
                  <button onClick={() => removeFromCart(item.id)} style={styles.removeButton}>
                    Remove
                  </button>
                </li>
              ))}
            </ul>
            <h4>Total: ${totalPrice}</h4>

            {/* Payment Mode */}
            <div style={styles.paymentMode}>
              <h4>Select Payment Mode:</h4>
              <select value={paymentMode} onChange={handlePaymentChange} style={styles.paymentSelect}>
                <option value="">-- Select Payment Mode --</option>
                <option value="Credit Card">Credit Card</option>
                <option value="Debit Card">Debit Card</option>
                <option value="Cash on Delivery">Cash on Delivery</option>
                <option value="UPI">UPI</option>
              </select>
              {formError && <p style={styles.error}>{formError}</p>}
            </div>

            {/* Checkout Button */}
            <button onClick={handleCheckout} style={styles.checkoutButton}>
              Checkout
            </button>
          </>
        ) : (
          <p>Your cart is empty</p>
        )}
      </section>

      {/* Payment Receipt */}
      {showReceipt && (
        <section style={styles.receiptSection}>
          <h3>Payment Receipt</h3>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                {item.name} (x{item.quantity}) - ${item.price * item.quantity}
              </li>
            ))}
          </ul>
          <p>Total Paid: ${totalPrice}</p>
          <p>Payment Method: {paymentMode}</p>
          <button onClick={() => {
            setCart([]); // Clear cart
            setPaymentMode(""); // Clear payment mode
            setShowReceipt(false); // Hide receipt
          }} style={styles.resetButton}>
            Done
          </button>
        </section>
      )}
    </div>
  );

};
// Inline CSS styles
const styles = {
  pageContainer: {
    fontFamily: "Arial, sans-serif",
    padding: "20px",
  },
  searchBar: {
    marginBottom: "20px",
    textAlign: "center",
  },
  searchInput: {
    padding: "10px",
    fontSize: "16px",
    width: "300px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  productSection: {
    textAlign: "center",
  },
  productList: {
    display: "flex",
    justifyContent: "center",
    gap: "20px",
    flexWrap: "wrap",
  },
  productItem: {
    border: "1px solid #ccc",
    padding: "10px",
    borderRadius: "10px",
    width: "150px",
    textAlign: "center",
  },
  productImage: {
    width: "100px",
    height: "100px",
    objectFit: "cover",
  },
  addButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "8px 16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  cartSection: {
    marginTop: "30px",
  },
  cartList: {
    listStyle: "none",
    paddingLeft: "0",
  },
  cartItem: {
    marginBottom: "10px",
  },
  removeButton: {
    backgroundColor: "#f44336",
    color: "white",
    padding: "6px 12px",
    border: "none",
    borderRadius: "5px",
    marginLeft: "10px",
    cursor: "pointer",
  },
  paymentMode: {
    margin: "20px 0",
  },
  paymentSelect: {
    padding: "10px",
    fontSize: "16px",
    width: "200px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  checkoutButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  error: {
    color: "red",
  },
  receiptSection: {
    marginTop: "30px",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
  },
  resetButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default UserProductViews;
