import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const stripePromise = loadStripe('your_stripe_publishable_key');

const PaymentForm = () => {
  const [clientSecret, setClientSecret] = useState('');
  const [amount, setAmount] = useState(0);
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [paymentType, setPaymentType] = useState('card');
  const [address, setAddress] = useState('');
  const [isAddressEditing, setIsAddressEditing] = useState(false); // To toggle editing

  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate(); // To navigate to address.js

  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  useEffect(() => {
    setAmount(totalPrice * 100);
  }, [totalPrice]);

  useEffect(() => {
    const fetchClientSecret = async () => {
      if (amount > 0) {
        try {
          const response = await fetch('http://localhost:8000/medical_store_app/create-payment-intent/', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ amount }),
          });
          const data = await response.json();
          if (response.ok) {
            setClientSecret(data.clientSecret);
          } else {
            setError(data.error || 'Failed to create payment intent');
          }
        } catch (err) {
          setError('Network error. Please try again.');
        }
      }
    };
    fetchClientSecret();
  }, [amount]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (paymentType === 'cod') {
      alert('Order placed successfully. Please pay on delivery.');
      return;
    }

    if (paymentType === 'card') {
      if (!stripe || !elements || !clientSecret) return;

      setProcessing(true);

      const { error: stripeError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: elements.getElement(CardElement) },
      });

      if (stripeError) {
        setError(stripeError.message);
      } else if (paymentIntent.status === 'succeeded') {
        alert('Payment successful!');
      }

      setProcessing(false);
    }
  };

  const handleEditAddress = () => {
    // Navigate to address.js and pass the current address
    navigate("/Address", { state: { address } });
  };

  const handleSaveAddress = (newAddress) => {
    setAddress(newAddress); // Update the address in the PaymentForm
    setIsAddressEditing(false);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      
      {/* Address Section */}
      <div style={styles.addressSection}>
        <label style={styles.label}>Delivery Address:</label>
        {isAddressEditing ? (
          <div>
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Enter your delivery address"
              style={styles.input}
            />
            <button
              onClick={handleSaveAddress}
              style={styles.button}
            >
              Save Address
            </button>
          </div>
        ) : (
          <div>
            <p>{address || 'No address provided'}</p>
            <button
              onClick={handleEditAddress}
              style={styles.button}
            >
              Edit Address
            </button>
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Payment Type:</label>
        <select
          value={paymentType}
          onChange={(e) => setPaymentType(e.target.value)}
          style={styles.input}
        >
          <option value="card">Card</option>
          <option value="cod">Cash on Delivery</option>
        </select>

        <label style={styles.label}>Amount:</label>
        <input type="number" value={amount / 100} readOnly style={styles.input} />

        {paymentType === 'card' && <CardElement />}

        <button
          type="submit"
          disabled={processing || !stripe || (paymentType === 'card' && !clientSecret) || (paymentType === 'card' && !elements.getElement(CardElement))}
          style={{
            ...styles.button,
            ...(processing || !stripe || (paymentType === 'card' && !clientSecret) || (paymentType === 'card' && !elements.getElement(CardElement)) ? styles.buttonDisabled : {}),
          }}
        >
          {processing ? 'Processing...' : 'Pay'}
        </button>
      </form>
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
};

const PaymentPage = () => (
  <Elements stripe={stripePromise}>
    <PaymentForm />
  </Elements>
);

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
  addressSection: {
    width: '100%',
    marginBottom: '20px',
  },
};

export default PaymentPage;
