// Booking.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams, useLocation } from "react-router-dom";

const Booking = () => {
  const [bookings, setBookings] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false); // New state for checking if the user is an admin
  const navigate = useNavigate();
  const { userId } = useParams();  // Extract customer id from URL parameters
  const location = useLocation();  // To access the passed order data

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8000/medical_store_app/bookings/${userId}/`
        );

        // Set default status to "pending" if not available
        const updatedBookings = response.data.bookings.map(booking => ({
          ...booking,
          status: booking.status || "pending",  // Set default to "pending"
        }));

        setBookings(updatedBookings);

        // Check if the current user is an admin (you can modify this logic based on your requirements)
        const userRole = sessionStorage.getItem("user_role"); // Assume role is stored in session
        if (userRole === 'admin') {
          setIsAdmin(true);
        }

      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, [userId]);

  const handleBackToHome = () => {
    navigate("/UserProductViews");
  };

  const handleStatusChange = async (bookingId, newStatus) => {
    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/admin/bookings/${bookingId}/update/`,
        { status: newStatus }
      );

      setBookings(
        bookings.map((booking) =>
          booking.id === bookingId ? { ...booking, status: newStatus } : booking
        )
      );
    } catch (error) {
      console.error("Error updating status:", error);
    }
  };

  return (
    <div style={styles.container}>
      <button style={styles.backButton} onClick={handleBackToHome}>
        Back to Shopping
      </button>
      <h1 style={styles.heading}>Your Bookings</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Customer ID</th>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Price</th>
            <th style={styles.tableHeader}>Status</th>
            <th style={styles.tableHeader}>Booking Date</th>
            {isAdmin && <th style={styles.tableHeader}>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.id}>
              <td>{booking.customer ? booking.customer.id : "N/A"}</td>
              <td>{booking.product.name}</td>
              <td>{booking.quantity}</td>
              <td>${(booking.product.price).toFixed(2)}</td>
              <td>
                <span style={getStatusStyle(booking.status)}>{booking.status}</span>
              </td>
              <td>{new Date(booking.booking_date).toLocaleString()}</td>
              {isAdmin && (
                <td>
                  <select
                    value={booking.status}
                    onChange={(e) => handleStatusChange(booking.id, e.target.value)}
                    style={styles.statusSelect}
                  >
                    <option value="pending">Pending</option>
                    <option value="confirmed">Confirmed</option>
                    <option value="rejected">Rejected</option>
                  </select>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const getStatusStyle = (status) => {
  switch (status) {
    case "confirmed":
      return { color: "green", fontWeight: "bold" };
    case "pending":
      return { color: "orange", fontWeight: "bold" };
    case "rejected":
      return { color: "red", fontWeight: "bold" };
    default:
      return { color: "gray", fontWeight: "normal" };
  }
};

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  backButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  statusSelect: {
    padding: "5px",
    borderRadius: "5px",
    fontSize: "14px",
    cursor: "pointer",
  },
};

export default Booking;
