import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios"; // Ensure this is the correct API URL

const Register = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    username: '',
    mobile_no: '',
    email: '',
    password: '',
    cpassword: ''
  });
  const [formError, setFormError] = useState({});
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate password matching
    if (formData.password !== formData.cpassword) {
      setFormError({ password: "Passwords do not match" });
      return;
    }

    try {
      const response = await axios.post(`${apiUrl}/register/`, formData);
      console.log(response.data); // Log success response
      setMessage(response.data.message);
    } catch (error) {
      console.error('Error:', error); // Log error for debugging
      setMessage(error.response?.data?.error || "Something went wrong.");
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)",
      }}
    >
      <div
        className="container mt-5"
        style={{
          width: "430px",
          height: "auto",
          boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)",
          backgroundColor: "#ffffff",
          padding: "40px",
          borderRadius: "15px",
          border: "2px solid #007bff",
        }}
      >
        <form onSubmit={handleSubmit} method="post" className="form">
          <div className="row mt-3 mb-3">
            <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" ,textAlign:"center"}}>
              <i className="bi bi-person-plus px-3"></i>Register
            </h2>
          </div>

          {/* Form Fields ... (unchanged) */}

          {/* Submit Button */}
          <div className="text-center">
            <button
              type="submit"
              className="btn btn-dark"
              style={{
                width: "50%",
                fontSize: "17px",
                padding: "10px 20px",
                backgroundColor: "#007bff",
                border: "none",
                borderRadius: "7px",
              }}
            >
              Register
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Register;
