// Cart.js
import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Cart({ cart, setCart }) {
  const navigate = useNavigate();

  // Remove a medicine from the cart
  const removeFromCart = (id) => {
    const updatedCart = cart.filter((medicine) => medicine.id !== id);
    setCart(updatedCart);
  };

  // Calculate total price
  const totalPrice = cart.reduce((total, medicine) => total + medicine.price, 0);

  return (
    <div style={styles.cartPage}>
      <h2 style={styles.cartTitle}>Your Cart</h2>
      {cart.length === 0 ? (
        <p style={styles.emptyCart}>
          Your cart is empty. <Link to="/">Continue shopping</Link>
        </p>
      ) : (
        <>
          <ul style={styles.cartList}>
            {cart.map((medicine) => (
              <li key={medicine.id} style={styles.cartItem}>
                <h3 style={styles.medicineName}>{medicine.name}</h3>
                <p style={styles.price}>Price: ${medicine.price.toFixed(2)}</p>
                <button 
                  onClick={() => removeFromCart(medicine.id)} 
                  style={styles.removeButton}>
                  Remove
                </button>
              </li>
            ))}
          </ul>
          <div style={styles.cartTotal}>
            <h3>Total: ${totalPrice.toFixed(2)}</h3>
            <button 
              style={styles.checkoutButton} 
              onClick={() => navigate("/checkout")}>
              Proceed to Checkout
            </button>
          </div>
        </>
      )}
    </div>
  );
}

// Inline CSS styles
const styles = {
  cartPage: {
    maxWidth: "600px",
    margin: "2em auto",
    padding: "1em",
    background: "#fff",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
  cartTitle: {
    fontSize: "24px",
    marginBottom: "1em",
    textAlign: "center",
  },
  emptyCart: {
    textAlign: "center",
    fontSize: "16px",
  },
  cartList: {
    listStyle: "none",
    padding: "0",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0.5em 0",
    borderBottom: "1px solid #ddd",
  },
  medicineName: {
    fontSize: "18px",
  },
  price: {
    fontSize: "16px",
  },
  removeButton: {
    padding: "5px 10px",
    color: "#fff",
    backgroundColor: "#e74c3c",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  cartTotal: {
    marginTop: "1em",
    fontSize: "18px",
    textAlign: "right",
  },
  checkoutButton: {
    padding: "10px 20px",
    color: "#fff",
    backgroundColor: "#2ecc71",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};
