import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../axios";

export default function ListMedicines() {
  const [medicines, setMedicines] = useState([]);
  const [loading, setLoading] = useState(true);

  const deleteMedicine = async (id) => {
    await axiosInstance
      .delete(apiUrl + `/medical_store_app/medicine/${id}/`)
      .then((response) => {
        if (response.status === 204) { // Check for 204 No Content response
          alert("Medicine deleted successfully!");
          getMedicines(); // Refresh list
        }
      })
      .catch((error) => {
        console.error("Error deleting medicine:", error);
      });
  };

  const getMedicines = async () => {
    await axiosInstance
      .get(apiUrl + `/medical_store_app/medicine/`)
      .then((response) => {
        if (response.status === 200) {
          setMedicines(response.data);
          setLoading(false);
        }
      })
      .catch((error) => {
        if (error.response?.status === 401) {
          generateRefreshToken(error);
        } else {
          console.error("Error fetching medicines:", error);
        }
      });
  };

  useEffect(() => {
    getMedicines();
  }, []);

  return (
    <div>
      <div className="container m-5">
        <div className="mt-3">
          {loading ? (
            <p>Loading medicines...</p>
          ) : (
            <table className="table table-striped" style={{ border: '1px solid #ddd' }}>
              <thead className="thead-dark">
                <tr>
                  <th style={{ textAlign: 'center' }}>SL No</th>
                  <th style={{ textAlign: 'center' }}>Medicine Name</th>
                  <th style={{ textAlign: 'center' }}>Category</th>
                  <th style={{ textAlign: 'center' }}>Price</th>
                  <th style={{ textAlign: 'center' }}>Description</th>
                  <th style={{ textAlign: 'center' }}>Stock Quantity</th>
                  <th style={{ textAlign: 'center' }}>Action</th>
                </tr>
              </thead>
              <tbody>
                {medicines.map((medicine, i) => (
                  <tr key={`medicine_row_${i}`}>
                    <td style={{ textAlign: 'center' }}>{i + 1}</td>
                    <td style={{ textAlign: 'center' }}>{medicine.medicine_name}</td>
                    <td style={{ textAlign: 'center' }}>{medicine.category}</td>
                    <td style={{ textAlign: 'center' }}>{medicine.price}</td>
                    <td style={{ textAlign: 'center' }}>{medicine.description}</td>
                    <td style={{ textAlign: 'center' }}>{medicine.stock_quantity}</td>
                    <td style={{ textAlign: 'center' }}>
                      <a
                        href="#"
                        onClick={() => deleteMedicine(medicine.id)}
                        className="text-danger"
                      >
                        <i className="bi bi-trash3"></i> Delete
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}
