import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { cartItems, totalPrice } = location.state || { cartItems: [], totalPrice: 0 };

  const handleConfirmPayment = async () => {
    try {
      // Redirect to payment gateway
      navigate("/checkout.razorpay.com/v1/checkout.js", { state: { cartItems, totalPrice } });
    } catch (error) {
      console.error("Error during payment confirmation:", error);
    }
  };

  const handleGoBack = () => {
    navigate("/cart");
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Checkout</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Price</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td style={styles.tableData}>{item.medicine.name}</td>
              <td style={styles.tableData}>{item.quantity}</td>
              <td style={styles.tableData}>₹{item.medicine.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total Price: ₹{totalPrice}</h3>
      <div style={styles.buttons}>
        <button style={styles.goBackButton} onClick={handleGoBack}>
          Go Back
        </button>
        <button style={styles.confirmButton} onClick={handleConfirmPayment}>
          Confirm & Pay
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: "50px",
    fontFamily: "Arial, sans-serif",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  tableData: {
    padding: "10px",
    borderBottom: "1px solid #ddd",
  },
  total: {
    fontSize: "1.5rem",
    textAlign: "right",
    marginTop: "20px",
    fontWeight: "bold",
  },
  buttons: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "20px",
  },
  goBackButton: {
    backgroundColor: "#f44336",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  confirmButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Checkout;
