import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { order, totalPrice, clearCart } = location.state;

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.onload = () => {
      const initializeRazorpay = () => {
        const options = {
          key: "rzp_test_a6hVvFHJbT9SAO",  // Your Razorpay key
          amount: totalPrice * 100,  // Amount in paise
          currency: "INR",
          name: "Your Company Name",
          description: "Payment for Order #" + order.id,
          order_id: order.id,
          handler: async function (response) {
            const { razorpay_payment_id, razorpay_order_id, razorpay_signature } = response;

            alert("Payment successful");

            // Send the payment details to the backend
            const paymentData = {
              razorpay_payment_id,
              razorpay_order_id,
              razorpay_signature,
              amount: totalPrice * 100,  // Amount in paise
            };

            try {
              await axios.post("http://localhost:8000/medical_store_app/payment_details/", paymentData);
              if (clearCart) {
                clearCart(); // Clear cart after successful payment
              }
              navigate("/booking", {
                state: {
                  order,
                  paymentDetails: paymentData,
                },
              });
            } catch (error) {
              console.error("Error processing payment:", error);
              alert("Payment processing failed.");
            }
          },
          prefill: {
            name: "Customer Name",
            email: "customer@example.com",
            contact: "9999999999",
          },
          theme: {
            color: "#F37254",
          },
        };
        const razorpay = new window.Razorpay(options);
        razorpay.open();
      };
      initializeRazorpay();
    };

    script.onerror = () => {
      alert("Failed to load Razorpay script.");
    };

    document.body.appendChild(script);
  }, [order, totalPrice, navigate, clearCart]);

  return (
    <div>
      <h2>Checkout</h2>
      {/* Add your checkout UI */}
    </div>
  );
};

export default Checkout;
