import React from "react";
import { useLocation } from "react-router-dom";

const Booking = () => {
  const location = useLocation();
  const { order = {}, paymentDetails = {} } = location.state || {};

  const formatAmount = (amount) => (amount ? (amount / 100).toFixed(2) : "N/A");

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>Booking Details</h1>
      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={headerStyle}>Field</th>
            <th style={headerStyle}>Value</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td style={cellStyle}>Order ID</td>
            <td style={cellStyle}>{order.id || "N/A"}</td>
          </tr>
          <tr>
            <td style={cellStyle}>Total Price</td>
            <td style={cellStyle}>₹{formatAmount(order.amount)}</td>
          </tr>
          <tr>
            <td style={cellStyle}>Payment ID</td>
            <td style={cellStyle}>
              {paymentDetails.razorpay_payment_id || "N/A"}
            </td>
          </tr>
          <tr>
            <td style={cellStyle}>Order ID</td>
            <td style={cellStyle}>
              {paymentDetails.razorpay_order_id || "N/A"}
            </td>
          </tr>
          <tr>
            <td style={cellStyle}>Amount Paid</td>
            <td style={cellStyle}>₹{formatAmount(paymentDetails.amount)}</td>
          </tr>
          <tr>
            <td style={cellStyle}>Signature</td>
            <td style={cellStyle}>
              {paymentDetails.razorpay_signature || "N/A"}
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

const containerStyle = {
  padding: "40px",
  fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  backgroundColor: "#f9f9f9",
  minHeight: "100vh",
};

const titleStyle = {
  textAlign: "center",
  marginBottom: "20px",
  color: "#333",
  fontSize: "24px",
};

const tableStyle = {
  width: "100%",
  borderCollapse: "collapse",
  backgroundColor: "white",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  borderRadius: "8px",
  overflow: "hidden",
};

const headerStyle = {
  backgroundColor: "#007bff",
  color: "white",
  padding: "15px",
  textAlign: "left",
  fontWeight: "bold",
  borderBottom: "2px solid #ddd",
};

const cellStyle = {
  padding: "15px",
  borderBottom: "1px solid #ddd",
  color: "#333",
  textAlign: "left",
};

export default Booking;
