import React, { useState, useEffect } from 'react';

const PaymentPage = () => {
  const [totalAmount, setTotalAmount] = useState(0);
  const [paymentMethod, setPaymentMethod] = useState('creditCard'); // default to credit card
  const [paymentDetails, setPaymentDetails] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: ''
  });

  useEffect(() => {
    // Get the total amount from the Cart (via props or API)
    // In this case, I am assuming you're passing it as a prop or fetching it from an API
    const fetchTotalAmount = async () => {
      const response = await fetch('/api/cart/total');
      const data = await response.json();
      setTotalAmount(data.totalAmount);
    };
    fetchTotalAmount();
  }, []);

  const handlePayment = async () => {
    // Send payment details to backend for processing
    const response = await fetch('/api/payment', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        totalAmount,
        paymentMethod,
        paymentDetails
      }),
    });

    const data = await response.json();
    if (data.success) {
      alert('Payment successful!');
    } else {
      alert('Payment failed, please try again.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPaymentDetails((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div>
      <h2>Payment Page</h2>
      <p>Total Amount: ${totalAmount}</p>
      
      <form onSubmit={(e) => e.preventDefault()}>
        {paymentMethod === 'creditCard' && (
          <>
            <label>Card Number</label>
            <input
              type="text"
              name="cardNumber"
              value={paymentDetails.cardNumber}
              onChange={handleChange}
              required
            />
            <label>Expiry Date</label>
            <input
              type="text"
              name="expiryDate"
              value={paymentDetails.expiryDate}
              onChange={handleChange}
              required
            />
            <label>CVV</label>
            <input
              type="text"
              name="cvv"
              value={paymentDetails.cvv}
              onChange={handleChange}
              required
            />
          </>
        )}

        <button type="button" onClick={handlePayment}>Pay Now</button>
      </form>
    </div>
  );
};

export default PaymentPage;
