import React, { useState } from 'react';

// Sample data (you can replace this with data fetched from an API or backend)
const sampleProducts = [
  {
    id: 1,
    name: 'Paracetamol',
    price: '5.00',
    description: 'Used for pain relief and fever reduction.',
    inStock: true,
  },
  {
    id: 2,
    name: 'Ibuprofen',
    price: '10.00',
    description: 'Used to reduce inflammation and treat pain.',
    inStock: false,
  },
  {
    id: 3,
    name: 'Aspirin',
    price: '8.50',
    description: 'Used as a pain reliever and to reduce fever or inflammation.',
    inStock: true,
  },
];

const AddProduct = ({ onProductAdded }) => { // Accept onProductAdded as a prop
  const [products, setProducts] = useState(sampleProducts);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    description: '',
    inStock: true,
  });

  const addProduct = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newProduct),
      });

      if (!response.ok) {
        const errorMessage = await response.text(); // Get error message from response
        throw new Error(`Failed to add product: ${errorMessage}`);
      }

      const addedProduct = await response.json();
      setProducts((prevProducts) => [...prevProducts, addedProduct]);
      setNewProduct({ name: '', price: '', description: '', inStock: true });
      alert(`${newProduct.name} has been added!`);

      // Call the function passed down from parent to update product list in Product.js
      onProductAdded(addedProduct); 
      
    } catch (error) {
      console.error('Error:', error);
      alert('Error adding product: ' + error.message);
    }
  };

  return (
    <div>
      <h1>Add New Product</h1>
      <form onSubmit={addProduct}>
        <input
          type="text"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Price"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          required
        />
        <textarea
          placeholder="Description"
          value={newProduct.description}
          onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
          required
        />
        <label>
          <input
            type="checkbox"
            checked={newProduct.inStock}
            onChange={(e) => setNewProduct({ ...newProduct, inStock: e.target.checked })}
          />
          In Stock
        </label>
        <button type="submit">Add Product</button>
      </form>

      <h2>Current Products</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>{product.name} - ${product.price}</li>
        ))}
      </ul>
    </div>
  );
};

export default AddProduct;
