import React, { useState, useEffect } from 'react';
import AddProduct from './addproduct'; // Import AddProduct component

const Product = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null); // State to hold error message

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/products'); // Fetch products from the backend

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`); // Handle non-2xx responses
        }

        const data = await response.json();
        setProducts(data); // Assuming the response is an array of products
      } catch (error) {
        setError(error.message); // Set error message in state
        console.error('Fetch error:', error); // Log error for debugging
      }
    };

    fetchProducts();
  }, []);

  const handleProductAdded = (newProduct) => {
    setProducts((prevProducts) => [...prevProducts, newProduct]); // Update the product list
  };

  return (
    <div>
      <AddProduct onProductAdded={handleProductAdded} /> {/* Pass handleProductAdded to AddProduct */}
      <h2>Product List</h2>
      {error && <p style={{ color: 'red' }}>Error: {error}</p>} {/* Display error message */}
      <ul>
        {products.map((product) => (
          <li key={product.id}>{product.name} - ${product.price}</li>
        ))}
      </ul>
    </div>
  );
};

export default Product;
