import React, { useEffect, useState } from 'react';

const UserProductViews = () => {
    const [medicines, setMedicines] = useState([]);

    const fetchMedicines = async () => {
        try {
            const response = await fetch('http://localhost:8000/medical_store_app/api/medicine/');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setMedicines(data); // Store fetched data in state
            console.log('Fetched Medicines:', data); // Debugging line
        } catch (error) {
            console.error('Error fetching medicines:', error);
        }
    };

    useEffect(() => {
        fetchMedicines(); // Fetch medicines on component mount
    }, []);

    const handleAddToCart = (medicine) => {
        console.log('Added to cart:', medicine);
        // Add the functionality to add the medicine to the cart here
    };

    const handleBuyNow = (medicine) => {
        console.log('Buying now:', medicine);
        // Add the functionality for buying the medicine directly here
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Available Medicines</h1>
            {medicines.length === 0 ? (
                <p>No medicines found.</p> // Corrected conditional check
            ) : (
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
                    {medicines.map((medicine) => (
                        <div
                            key={medicine.id}
                            style={{
                                border: '1px solid #ddd',
                                borderRadius: '8px',
                                padding: '20px',
                                width: '200px',
                                textAlign: 'center',
                                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                            }}
                        >
                            <h3>{medicine.name}</h3>
                            <p>{medicine.description}</p>
                            <p>Price: ${medicine.price}</p>
                            <div style={{ marginTop: '10px' }}>
                                <button
                                    onClick={() => handleAddToCart(medicine)}
                                    style={{
                                        backgroundColor: '#28a745',
                                        color: 'white',
                                        padding: '10px 15px',
                                        border: 'none',
                                        borderRadius: '4px',
                                        cursor: 'pointer',
                                        marginRight: '10px',
                                    }}
                                >
                                    Add to Cart
                                </button>
                                <button
                                    onClick={() => handleBuyNow(medicine)}
                                    style={{
                                        backgroundColor: '#007bff',
                                        color: 'white',
                                        padding: '10px 15px',
                                        border: 'none',
                                        borderRadius: '4px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    Buy Now
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default UserProductViews;
