import React, { useState } from "react";

function ListMedicine() {
  const [products, setProducts] = useState([]); 

  const handleDelete = (productName) => {
    setProducts(products.filter(product => product.name !== productName));
  };

  const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "20px",
  };

  const thStyle = {
    padding: "12px",
    backgroundColor: "#4CAF50",
    color: "white",
    textAlign: "left",
  };

  const tdStyle = {
    padding: "10px",
    borderBottom: "1px solid #ddd",
  };

  const deleteButtonStyle = {
    padding: "5px 10px",
    backgroundColor: "#f44336",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  };

  const containerStyle = {
    margin: "20px",
  };

  const titleStyle = {
    textAlign: "center",
    marginBottom: "20px",
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>View Product</h2>
      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={thStyle}>Medicine Name</th>
            <th style={thStyle}>Category</th>
            <th style={thStyle}>Price</th>
            <th style={thStyle}>Description</th>
            <th style={thStyle}>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.length === 0 ? (
            <tr>
              <td colSpan="6" style={{ textAlign: "center" }}>No products available</td>
            </tr>
          ) : (
            products.map(product => (
              <ProductRow
                key={product.name}
                product={product}
                onDelete={handleDelete}
                tdStyle={tdStyle}
                deleteButtonStyle={deleteButtonStyle}
              />
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

function ProductRow({ product, onDelete, tdStyle,deleteButtonStyle }) {
  return (
    <tr>
      {product.name && <td style={tdStyle}>{me.name}</td>}
      {product.category && <td style={tdStyle}>{product.category}</td>}
      {product.price && <td style={tdStyle}>{product.price}</td>}
      {product.description && <td style={tdStyle}>{product.description}</td>}
      <td style={tdStyle}>
        <button style={deleteButtonStyle} onClick={() => onDelete(product.name)}>
          Delete
        </button>
      </td>
    </tr>
  );
}

export default ListMedicine;
