import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import jwtDecode from "jwt-decode"; // Correct import for jwt-decode
import { apiUrl } from "../axios";

export default function Login() {
  const navigate = useNavigate();
  
  // State for form values
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  const [formError, setFormError] = useState(""); // To display form errors
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and registration

  // Login or Register submission handler
  const onSubmit = (e) => {
    e.preventDefault();
    const formValues = { username, password };
    
    // Perform login request
    if (isLogin) {
      axios
        .post(apiUrl + "/api/token/", formValues)
        .then((response) => {
          sessionStorage.setItem("access_token", response.data.access);
          sessionStorage.setItem("refresh_token", response.data.refresh);
          
          const user = jwtDecode(response.data.access); // Decode the token
          const userId = user.user_id; // Get user ID
          const username = user.name; // Get username

          // Store user information in session storage
          sessionStorage.setItem("user_id", userId);
          sessionStorage.setItem("name", username);

          // Navigate to home page
          navigate("/home");
        })
        .catch((error) => {
          if (error.response && error.response.status === 401) {
            setFormError(error.response.data.detail); // Show error message
          } else {
            setFormError("Something went wrong. Please try again.");
          }
        });
    } else {
      // Registration logic (if not login)
      const registrationData = { username, password, fname, lname, email };
      axios
        .post(apiUrl + "/register/", registrationData)
        .then(() => {
          alert("Registration successful! Please login.");
          setIsLogin(true); // Switch to login form after registration
        })
        .catch((error) => {
          setFormError(
            error.response?.data?.detail || "Registration failed. Try again."
          );
        });
    }
  };

  return (
    <div className="container" style={{ marginTop: "10em" }}>
      <h1 className="text-white">{isLogin ? "Login Page" : "Registration Page"}</h1>

      <hr className="bg-white" />
      
      {/* Display error if any */}
      {formError && <div className="alert alert-danger">{formError}</div>}
      
      <form onSubmit={onSubmit}>
        {/* Username field */}
        <label htmlFor="username" className="form-label text-light">
          Username
        </label>
        <input
          type="text"
          className="form-control"
          id="username"
          placeholder="Enter username..."
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        
        {!isLogin && (
          <>
            {/* Registration-specific fields */}
            <br />
            <label htmlFor="fname" className="text-light">First Name</label>
            <input
              type="text"
              className="form-control"
              id="fname"
              placeholder="Enter first name..."
              value={fname}
              onChange={(e) => setFname(e.target.value)}
            />
            <br />
            <label htmlFor="lname" className="text-light">Last Name</label>
            <input
              type="text"
              className="form-control"
              id="lname"
              placeholder="Enter last name..."
              value={lname}
              onChange={(e) => setLname(e.target.value)}
            />
            <br />
            <label htmlFor="email" className="text-light">Email</label>
            <input
              type="email"
              className="form-control"
              id="email"
              placeholder="Enter email..."
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </>
        )}

        {/* Password field */}
        <br />
        <label htmlFor="password" className="form-label text-light">
          Password
        </label>
        <input
          type="password"
          className="form-control"
          id="password"
          placeholder="Enter password..."
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <br />
        <button type="submit" className="btn btn-primary">
          {isLogin ? "Login" : "Register"}
        </button>
      </form>

      {/* Toggle between login and registration */}
      <div className="mb-3 text-white">
        <br />
        {isLogin ? (
          <h5>
            If you don't have an account,
            <button
              className="btn btn-link text-light"
              onClick={() => setIsLogin(false)}
            >
              Register
            </button>
          </h5>
        ) : (
          <h5>
            If you have an account,
            <button
              className="btn btn-link text-light"
              onClick={() => setIsLogin(true)}
            >
              Login
            </button>
          </h5>
        )}
      </div>
    </div>
  );
}
