import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [showProductDropdown, setShowProductDropdown] = useState(false);

  const toggleProductDropdown = () => {
    setShowProductDropdown(!showProductDropdown);
  };

  const styles = {
    navbar: {
      backgroundColor: '#2a2a2a',
      padding: '10px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      position: 'fixed', // Fix the navbar at the top
      top: 0,
      width: '100%',
      zIndex: 1000,
    },
    navLinks: {
      display: 'flex',
      alignItems: 'center',
      gap: '20px',
    },
    navLink: {
      color: 'white',
      textDecoration: 'none',
      padding: '10px 15px',
      borderRadius: '5px',
      transition: 'background-color 0.3s ease',
      fontWeight: 'bold',
      cursor: 'pointer',
    },
    dropdownMenu: {
      position: 'absolute',
      top: '40px',
      left: 0,
      backgroundColor: '#333',
      borderRadius: '8px',
      padding: '10px',
      zIndex: 1,
      boxShadow: '0px 4px 12px rgba(0,0,0,0.1)',
    },
    dropdownItem: {
      color: 'white',
      padding: '10px 15px',
      textDecoration: 'none',
      display: 'block',
      backgroundColor: '#444',
      margin: '5px 0',
      borderRadius: '4px',
      transition: 'background-color 0.3s ease',
    },
    logo: {
      color: 'white',
      fontSize: '20px',
      fontWeight: 'bold',
      textDecoration: 'none',
    },
  };

  return (
    <header style={styles.navbar}>
      <Link to="/" style={styles.logo}>
        Online Medical Shop Admin
      </Link>
      <nav style={styles.navLinks}>
        <Link to="/User" style={styles.navLink}>
          View User
        </Link>
        <Link to="/MedicineBookings" style={styles.navLink}>
          View Booking
        </Link>
        <Link to="/FeedbackList" style={styles.navLink}>
          Change Password
        </Link>
        <div style={{ position: 'relative' }}>
          <div
            style={styles.navLink}
            onClick={toggleProductDropdown}
          >
            Medicines â–¼
          </div>
          {showProductDropdown && (
            <div style={styles.dropdownMenu}>
              <Link to="/ad/AddEditMedicine" style={styles.dropdownItem}>
                Add/Edit Medicine
              </Link>
              <Link to="/ListMedicine" style={styles.dropdownItem}>
                List Medicines
              </Link>
            </div>
          )}
        </div>
        <>
              <h6 className="px-2">
                <i className="bi bi-person-circle px-3"></i>Welcome,{" "}
                {sessionStorage.getItem("name")}
              </h6>
              <button onClick={handleLogout} className="nav-button">Logout</button>
            </>
      </nav>
    </header>
  );
};

export default Navbar;
