import React, { useState } from 'react';

const AddressForm = ({ onAddressSubmit }) => {
  const [address, setAddress] = useState({
    street_address: '',
    city: '',
    state: '',
    postal_code: '',
    country: '',
  });

  const handleChange = (e) => {
    setAddress({ ...address, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/medical_store_app/add-address/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(address),
      });
      if (response.ok) {
        alert('Address added successfully');
        onAddressSubmit();
      } else {
        alert('Failed to add address');
      }
    } catch (error) {
      alert('Error submitting address');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Add Address</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Street Address:</label>
        <input
          type="text"
          name="street_address"
          value={address.street_address}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>City:</label>
        <input
          type="text"
          name="city"
          value={address.city}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>State:</label>
        <input
          type="text"
          name="state"
          value={address.state}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>Postal Code:</label>
        <input
          type="text"
          name="postal_code"
          value={address.postal_code}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>Country:</label>
        <input
          type="text"
          name="country"
          value={address.country}
          onChange={handleChange}
          style={styles.input}
        />

        <button type="submit" style={styles.button}>
          Submit Address
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default AddressForm;
