// src/components/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const Login = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [credentials, setCredentials] = useState({
    username: '',
    password: '',
  });
  const navigate = useNavigate(); // Initialize navigate

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const endpoint = isAdmin ? '/api/admin/login/' : '/api/user/login/';
    try {
      const response = await axios.post(endpoint, credentials);
      console.log('Login successful:', response.data);
      // Redirect to dashboard or homepage based on the user type
      if (isAdmin) {
        navigate('/admin/dashboard');  // Redirect admin to admin dashboard
      } else {
        navigate('/user/dashboard');  // Redirect user to user dashboard
      }
    } catch (error) {
      console.error('Login failed:', error.response?.data || error.message);
      // Handle error, e.g., show an error message
    }
  };

  // Inline CSS styles
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      backgroundColor: '#f4f4f4',
    },
    form: {
      background: '#fff',
      padding: '20px',
      borderRadius: '5px',
      boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
      width: '300px',
      textAlign: 'center',
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: 'bold',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      border: '1px solid #ccc',
      borderRadius: '4px',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#007BFF',
      color: '#fff',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontWeight: 'bold',
    },
    switchButton: {
      marginTop: '10px',
      padding: '10px',
      backgroundColor: '#6c757d',
      color: '#fff',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontWeight: 'bold',
    },
  };

  return (
    <div style={styles.container}>
      <h2>{isAdmin ? 'Admin Login' : 'User Login'}</h2>
      <form style={styles.form} onSubmit={handleLogin}>
        <div>
          <label style={styles.label}>
            Username:
            <input
              type="text"
              name="username"
              value={credentials.username}
              onChange={handleInputChange}
              required
              style={styles.input}
            />
          </label>
        </div>
        <div>
          <label style={styles.label}>
            Password:
            <input
              type="password"
              name="password"
              value={credentials.password}
              onChange={handleInputChange}
              required
              style={styles.input}
            />
          </label>
        </div>
        <button type="submit" style={styles.button}>Login</button>
      </form>
      <button style={styles.switchButton} onClick={() => setIsAdmin(!isAdmin)}>
        Switch to {isAdmin ? 'User' : 'Admin'} Login
      </button>
    </div>
  );
};

export default Login;
