import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PaymentPage = ({ userId, medicines }) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [totalAmount, setTotalAmount] = useState(0);

    // Calculate the total amount based on the medicines in the cart
    useEffect(() => {
        const calculateTotal = () => {
            let total = 0;
            medicines.forEach((medicine) => {
                total += medicine.price * medicine.quantity;  // Adjust according to your data structure
            });
            setTotalAmount(total);
        };

        calculateTotal();
    }, [medicines]);  // Recalculate whenever medicines change

    const handlePlaceOrder = async () => {
        try {
            setIsLoading(true);
            const response = await axios.post(
                'http://localhost:8000/medical_store_app/orders/',  // The API endpoint for placing an order
                {
                    user_id: userId,
                    medicines: medicines.map(m => m.id),  // Assuming medicines have 'id' property
                    total_amount: totalAmount,
                    payment_type: 'COD',  // Hardcoding COD for this example
                },
                {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('authToken')}`,  // Assuming JWT for authentication
                    },
                }
            );

            console.log(response.data);  // Handle success
            alert('Order placed successfully!');
        } catch (err) {
            console.error(err);
            setError('Something went wrong. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h1>Payment Page</h1>
            <p>Payment Method: Cash on Delivery (COD)</p>
            <p>Total Amount: ${totalAmount.toFixed(2)}</p>
            
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <button onClick={handlePlaceOrder} disabled={isLoading}>
                {isLoading ? 'Placing Order...' : 'Place Order'}
            </button>
        </div>
    );
};

export default PaymentPage;
